    private void enviarActionPerformed(java.awt.event.ActionEvent evt) {                                       
        String mensaje,textAns="";
        String stringNumbers;
        double cuadrado,raiz,cubo,num;
        try{
            stringNumbers=ourInput.getText();
            mensaje="Datos introducidos correctamente";
            mensajeAns.setText(mensaje);

            List<String> numeros = new ArrayList<String>(Arrays.asList(stringNumbers.split(" ")));

            for (int i=0;i<numeros.size();i++) {
                num=Double.parseDouble(numeros.get(i)) ;
                cuadrado=Math.pow(num, 2);
                raiz=Math.sqrt(num);
                cubo=Math.pow(num, 3);
                textAns=textAns+"Del numero: "+num+"\n"+"Con cuadrado: "+cuadrado+"\n"+"Con raiz: "+raiz+"\n"+"Con cubo: "+cubo+"\n ----- \n";
            }
            textoRespuesta.setText(textAns);
        }
        catch(Exception e){
            mensaje="Entradas no vÃ¡lidas"+e;
            mensajeAns.setText(mensaje);
        }
    }                                      

