    private void enviarCirculoActionPerformed(java.awt.event.ActionEvent evt) {                                              
        // TODO add your handling code here:
        String mensaje;
        double radio,perimetro,area;

        try{
            radio=Double.parseDouble(radioInput.getText());
            area=Math.pow(radio, 2)*Math.PI;
            perimetro=2*radio*Math.PI;
            mensaje="Datos introducidos correctamente";
            mensajeAns.setText(mensaje);
            circuloTextPane.setText("area: "+area+"\n"+"perimetro: "+perimetro);
        }
        catch(Exception e){
            mensaje="Entradas no válidas"+e;
            mensajeAns.setText(mensaje);
        }
    }                                             

