    private void enviarTrianguloRectanguloActionPerformed(java.awt.event.ActionEvent evt) {                                                          
        // TODO add your handling code here:
                // TODO add your handling code here:
        String mensaje,tipoTriangulo;
        double base,altura,area,perimetro,hipotenusa;

        try{
            base=Double.parseDouble(baseInput.getText());
            altura=Double.parseDouble(alturaInput.getText());
            
            area=(base*altura)/2;
            hipotenusa=Math.pow(base, 2)+Math.pow(altura, 2);
            perimetro=base+altura+hipotenusa;
            if ((base==altura) && (base == hipotenusa) && (altura == hipotenusa)){
                tipoTriangulo="Es un triangulo equilatero";
            } else if ((base != altura) && (base != hipotenusa) && (altura != hipotenusa)){
                tipoTriangulo="Es un triangulo escaleno";
            } else {
                tipoTriangulo="Es un triangulo isósceles";
            }
            
            mensaje="Datos introducidos correctamente";
            mensajeAns.setText(mensaje);
            trianguloRectanguloTextPane.setText("area: "+area+"\n"+"perimetro: "+perimetro+"\n"+"hipotenusa: "+hipotenusa+"\n"+"tipo: "+tipoTriangulo);
        }
        catch(Exception e){
            mensaje="Entradas no válidas"+e;
            mensajeAns.setText(mensaje);
        }
    }                                                         

